import React, { useState } from 'react';
import styles from '../styles/components/VersionMenu.module.css';

const VersionMenu = () => {
  const Date = ["1950","1951", "1952", "1953", "1954", "1955", "1956", "1957", "1958","1959"];
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredVersions, setFilteredVersions] = useState(Date);

  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    
    if (term === '') {
      setFilteredVersions(Date);
    } else {
      setFilteredVersions(
        Date.filter(version => 
          version.includes(term) || 
          version.replace('.', '').includes(term.replace('.', ''))
        )
      );
    }
  };

  return (
    <div className={styles['versions-menu']}>
      <h3>Года</h3>
      <input 
        type="text" 
        className={styles['version-search']} 
        placeholder="Поиск года..." 
        value={searchTerm}
        onChange={handleSearchChange}
      />
      {filteredVersions.length > 0 ? (
        filteredVersions.map(version => (
          <button 
            key={version} 
            className={styles['version-button']}
          >
            {version}
          </button>
        ))
      ) : (
        <div className={styles['no-results']}>
          Год не найден
        </div>
      )}
    </div>
  );
};

export default VersionMenu;
